/* this is the LeagueIcon object. */// Construction/Destruction //// On construction, read the color icons into the data structure// On destruction, free up the data space.CLeagueIcon::CLeagueIcon(){	int i;		for (i = Player1; i <= maxNumberOfPlayers; i++)		this->leagueIcon[i] = GetCIcon(leagueID[i]);}CLeagueIcon::~CLeagueIcon(){	int i;		for (i = Player1; i <= maxNumberOfPlayers; i++)		DisposCIcon(this->leagueIcon[i]);}// Accessor Functions//// The only one returns the color icon for the specified league.CIconHandle CLeagueIcon::GetLeagueIcon(short league){	return this->leagueIcon[league];}